package com.arms.common.util;/** * 页码相关工具类 *  * @author zhangchaojie * @since 2016-08-24 */public class PageUtil {    /**     * 计算页码总数     *     * @param totalElemNum 总元素个数     * @param perPageElemNum 每页元素个数     * @return 页码总数     */    public static int calcPageTotal(int totalElemNum, int perPageElemNum) {        return (totalElemNum == 0) ? 1 : ((totalElemNum - 1) / perPageElemNum + 1);    }    public static int getStart(int page, int pageSize) {        if (page > 0 && pageSize > 0) {            return (page - 1) * pageSize;        }        return 0;    }    public static int getLimit(int page, int pageSize) {        if (page > 0 && pageSize > 0) {            return pageSize;        }        return 0;    }}